import { useState } from "react";
import { useAuth } from "../AuthProvider";
import "../styles/Login.css";

const Login = () => {
  const [input, setInput] = useState({
    username: "",
    password: "",
  });
  const [mensaje, setMensaje] = useState("");
  const { loginAction } = useAuth();

  const handleSubmitEvent = (e) => {
    e.preventDefault();
    if (input.username !== "" && input.password !== "") {
      setMensaje("Usuario y contraseña incorrectos");
      loginAction(input)
        .then((response) => {
          if (response.error) {
            setMensaje(response.error);
          } else {
            setMensaje(`Bienvenido, ${response.nombre_usuario}`);
          }
        })
        .catch((error) => {
          console.error("Error al iniciar sesión:", error.message);
          setMensaje("Error al iniciar sesión, por favor intenta nuevamente");
        });
    } else {
      setMensaje("El usuario y la contraseña son obligatorios");
    }
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setInput((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="backgroundLG">
      <h1 className="title">GEMPRO</h1>
      <div className="login-containerLG">
        <div id="mensaje">{mensaje}</div>
        <h2>Inicio de Sesión</h2>
        <form id="datos" onSubmit={handleSubmitEvent}>
          <div className="form_controlLG">
            <label htmlFor="user-name">Usuario</label>
            <input
              type="text"
              id="user-name"
              name="username"
              placeholder="Ingrese su usuario"
              onChange={handleInput}
              value={input.username}
              required
            />
          </div>
          <div className="form_controlLG">
            <label htmlFor="password">Contraseña</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Ingrese su contraseña"
              onChange={handleInput}
              value={input.password}
              required
            />
          </div>
          <button type="submit" className="btn-submitLG">
            Iniciar sesión
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
