import { useState } from "react";
import { useAuth } from "../AuthProvider";
import '../styles/Login.css';

const Login = () => {
  const [input, setInput] = useState({
    username: "",
    password: "",
  });
  const [mensaje, setMensaje] = useState("");
  const auth = useAuth();

  const handleSubmitEvent = (e) => {
    e.preventDefault();
    if (input.username !== "" && input.password !== "") {
      auth.loginAction(input);
      setMensaje("Usuario y contraseña incorrectos");
      return;
    }
    alert("El usuario y la contraseña son obligatorios");
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setInput((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="background1">
      <h1 className="title">GEMPRO</h1>
      <div className="login-container1">
        <div id="mensaje">{mensaje}</div>
        <h2>Inicio de Sesión</h2>
        <form id="datos" onSubmit={handleSubmitEvent}>
          <div className="form_control">
            <label htmlFor="user-name"></label>
            <input
              type="text"
              id="user-name"
              name="username"
              aria-describedby="user-name"
              aria-invalid="false"
              placeholder="Usuario"
              onChange={handleInput}
            />
            <div id="user-name" className="sr-only"></div>
          </div>
          <div className="form_control">
            <label htmlFor="password"></label>
            <input
              type="password"
              id="password"
              name="password"
              aria-describedby="user-password"
              aria-invalid="false"
              placeholder="Contraseña"
              onChange={handleInput}
            />
            <div id="user-password" className="sr-only"></div>
          </div>
          <button className="btn-submit">Iniciar sesión</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
