import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useAuth } from "../AuthProvider";
import Navbar from "../components/NavBar";
import "../styles/Registers.css";

const RegisterFactura = () => {
    const location = useLocation();
    const total = location.state?.total || 0;
    const totalPagar = ((total * .19) + total);

    const [factura, setFactura] = useState({
        id_cliente: "",
        id_usuario: "",
        subtotal: total,
        total_pagar: totalPagar,
    });
    const [mensaje, setMensaje] = useState("");
    const { user, token } = useAuth();

    useEffect(() => {
        // Actualizar el subtotal y el total a pagar si cambia el total desde la ubicaci贸n
        setFactura((prevFactura) => ({
            ...prevFactura,
            subtotal: total,
            total_pagar: totalPagar,
        }));
    }, [total]);

    const handleSubmitEvent = async (e) => {
        e.preventDefault();

        if (factura.id_cliente.trim() !== "" && factura.id_usuario.trim() !== "") {
            try {
                const response = await fetch("http://localhost:3000/factura", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`,
                    },
                    body: JSON.stringify(factura),
                });
                const data = await response.json();
                if (response.ok) {
                    setMensaje(data.message);
                    setFactura({
                        id_cliente: "",
                        id_usuario: "",
                        subtotal: "",
                        total_pagar: "",
                    });
                } else {
                    setMensaje(data.message || "Error al registrar la factura");
                }
            } catch (error) {
                console.error("Error al registrar la factura:", error.message);
                setMensaje("Error de conexi贸n");
            }
        } else {
            setMensaje("Todos los campos son obligatorios");
        }
    };

    const handleInput = (e) => {
        const { name, value } = e.target;
        setFactura((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    return (
        <div>
            <Navbar />
            <div className="backgroundRCli">
                <div className="containerRCli">
                    <div id="mensaje">{mensaje}</div>
                    <h2 className="tituloU-C">Factura</h2>
                    <form id="datos" onSubmit={handleSubmitEvent}>
                        <div>
                            <p>Usuario: {user}</p>
                            <p>Subtotal: ${factura.subtotal.toLocaleString("es-ES")}</p>
                            <p>IVA: ${(factura.subtotal * 0.19).toLocaleString("es-ES")}</p>
                            <p>Total a Pagar: ${factura.total_pagar.toLocaleString("es-ES")}</p>
                            <button className="btn-submitRM">Realizar Compra</button>
                        </div>
                    </form>

                </div>
            </div>
        </div>
    );
};

export default RegisterFactura;
