import React from "react";
import { useLocation } from "react-router-dom";
import Navbar from "../components/NavBar";
import '../styles/Products.css';

const ProductoSeleccionado = () => {
  const location = useLocation();
  const { className, productName, imageUrl, precio, descripcion } = location.state || {};

  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="tituloPrdSel">{productName ? productName : "No se seleccionó ningún producto"}</h2>
        {className && imageUrl ? (
          <div className="productoSeleccionado">
            <div className="descripcion">{descripcion}</div>
            <img 
              className="imagenProdSeleccionado" 
              src={imageUrl} 
              alt={productName} 
            />
            <div className="precio">${precio}</div>
            <button className="btn-submitImgSel">Agregar al Carrito</button>
          </div>
        ) : (
          <p>No se seleccionó ninguna imagen.</p>
        )}
      </div>
    </div>
  );
};

export default ProductoSeleccionado;
