import React, { useEffect, useState } from "react";
import Navbar from "../components/NavBar";
import { useAuth } from "../AuthProvider";
import {useNavigate, useParams} from "react-router-dom";
import '../styles/Products.css';
import {productApi} from "../common";
import toastr from 'toastr';

const ProductsByCategory = () => {

    const { category } = useParams();
    const navigate = useNavigate();
    const [products, setProducts] = useState([]);
    const auth = useAuth();

    const getAllProductsByCategory = async () => {
        await fetch(`${productApi.getAllProductsByCategory.url}/${category}`, {
            method: productApi.getAllProductsByCategory.method,
            headers: {
                "Content-Type": "application/json"
            },
        })
            .then((res) => {
                if (res.status === 404 || res.status === 500)
                    navigate('/categorias');
                res.json()
            })
            .then((data) => {
                console.log(data)
                //const productosFiltrados = datos.data.filter(producto => ((producto.ID_CATEGORIA === '204')&& producto.CANTIDAD>0));
                setProducts(data.products);
            })
            .catch((error) => console.error(error));
    };

    useEffect(() => {
        getAllProductsByCategory();
    }, [auth.token]);

    const handleImageClick = (idProduct, productName, imageUrl, costo, cantidad, margen_ganancia) => {
        navigate("/productoSeleccionado", {
            state: { idProduct, productName, imageUrl, costo, cantidad, margen_ganancia }
        });
    };

    return (
        <div>
            <Navbar />
            <h3 className="titlePRODUCTOS">{category}</h3>
            <div className="containerPRODCUTOS">
                {products.map((product, index) => (
                    <div
                        key={product._id + index}
                        className="opcionPROD"
                        onClick={() => alert('')} //handleImageClick(`${producto.ID_PRODUCTO}`, producto.DESCRIPCION, `${process.env.REACT_APP_URL_IMG}${producto.IMAGEN}`,
                            //producto.COSTO, producto.CANTIDAD, producto.MARGEN_GANANCIA)}
                    >
                        <img
                            className="imagenProdSelecionadoP"
                            src={product?.image}
                            alt={product?.name}
                        />
                        <div className="productoTexto">{product?.name}</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductsByCategory;
