import React, { useState, useRef, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { useAuth } from "../AuthProvider";
import { useCarrito } from "../components/CarritoContext";
import "../styles/NavBar.css";

const NavBar = () => {
  const auth = useAuth();
  const { carrito } = useCarrito();
  const [showAdminMenu, setShowAdminMenu] = useState(false);
  const adminMenuRef = useRef(null);

  const totalProductos = carrito.reduce((total, item) => total + item.cantidad, 0);
  const userType = auth.user ? auth.user.ID_TIPO : null;

  // Función para manejar clics fuera del menú desplegable
  const handleClickOutside = (event) => {
    if (adminMenuRef.current && !adminMenuRef.current.contains(event.target)) {
      setShowAdminMenu(false);
    }
  };

  // Agregar un event listener para cerrar el menú al hacer clic fuera de él
  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Función para alternar la visibilidad del menú desplegable
  const toggleAdminMenu = () => {
    setShowAdminMenu(prev => !prev); // Alternamos el estado anterior
  };

  return (
    <div>
      <nav>
        <div className="navbar">
          <div className="logo">
            <NavLink to="/dashboard">
              <h1>JOYERIA D'LAURA</h1>
            </NavLink>
          </div>
          <div className="menu-items">
            <ul>
              {userType === "1" && (
                <li className="dropdown" ref={adminMenuRef}>
                  <div onClick={toggleAdminMenu}>Administrar</div>
                  <ul className={`dropdown-menu ${showAdminMenu ? 'show' : ''}`}>
                    <li>
                      <NavLink to="/administrar/usuarios">Usuarios</NavLink>
                    </li>
                    <li>
                      <NavLink to="/administrar/productos">Productos</NavLink>
                    </li>
                    {/* Agrega más opciones según necesites */}
                  </ul>
                </li>
              )}
              <li style={{ margin: '0px 0px 0px 10px' }}>
                <NavLink to="/Categorias">Categorías</NavLink>
              </li>
              <li>
                <NavLink to="/About">About</NavLink>
              </li>
              {auth.user && (
                <li>
                  {totalProductos > 0 ? (
                    <NavLink to="/carrito">Carrito ({totalProductos})</NavLink>
                  ) : (
                    <NavLink to="/carrito">Carrito</NavLink>
                  )}
                </li>
              )}
              <li>
                {auth.user ? (
                  <a href="#" onClick={() => auth.logOut()}>Salir</a>
                ) : (
                  <NavLink to="/Login">Ingresar</NavLink>
                )}
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default NavBar;
