import React, { useEffect, useState } from "react";
import Navbar from "../components/NavBar";
import { useAuth } from "../AuthProvider";
import { useNavigate } from "react-router-dom";
import '../styles/Products.css';

const ProductosAnilloSolitario = () => {
  const navigate = useNavigate();
  const [productos, setProductos] = useState([]);
  const auth = useAuth();

  useEffect(() => {
    fetch("http://localhost:3000/producto", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": auth.token,
      },
    })
      .then((res) => res.json())
      .then((datos) => {       
        const productosFiltrados = datos.data.filter(producto => producto.ID_CATEGORIA === '203');
        setProductos(productosFiltrados);
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, [auth.token]);
  
  const handleImageClick = (producto) => {
    navigate("/productoSeleccionado", {
      state: { 
        className: producto.ID_PRODUCTO,
        productName: producto.DESCRIPCION,
        imageUrl: `${process.env.REACT_APP_URL_IMG}${producto.IMAGEN}`
      }
    });
  };

  return (
    <div>
      <Navbar />
      <h3 className="titlePRODUCTOS">ANILLOS SOLITARIOS</h3>
      <div className="containerPRODCUTOS">
        {productos.map((producto) => (
          <div
            key={producto.ID_PRODUCTO}
            className={`${producto.ID_PRODUCTO}`}
            onClick={() => handleImageClick(`${producto.ID_PRODUCTO}`)}
          >
            <div>
              <img 
                className="imagenProdSelecionado" 
                src={`${process.env.REACT_APP_URL_IMG}${producto.IMAGEN}`} 
                alt={producto.DESCRIPCION} 
              />
              <div className="productoTexto">{producto.DESCRIPCION}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductosAnilloSolitario;
