import { createContext, useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCarrito } from "./components/CarritoContext";

const AuthContext = createContext();

const login = async function (data) {
  const usuario = { nombre_usuario: data.username, contrasena: data.password };
  const response = await fetch("http://localhost:3000/usuario/login", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(usuario),
  });
  return response.json();
};

const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("site") || "");
  const navigate = useNavigate();
  const { limpiarCarrito } = useCarrito();

  const loginAction = async (data) => {
    try {
      const response = await login(data);
      if (response.error) {
        throw new Error(response.error);
      }
      setUser(response.nombre_usuario);
      setToken(response.token);
      localStorage.setItem("site", response.token);
      console.log(user);
      navigate("/dashboard");
    } catch (error) {
      console.error("Error al iniciar sesión:", error.message);
      // Manejo de errores o mensajes de error
    }
  };

  const logOut = () => {
    setUser(null);
    setToken("");
    localStorage.removeItem("site");
    limpiarCarrito();
    navigate("/login");
  };

  return (
    
    <AuthContext.Provider value={{ user, token, loginAction, logOut }}>
      {children}
    </AuthContext.Provider>
    
  );
};

export default AuthProvider;

// Hook personalizado
export const useAuth = () => {
  return useContext(AuthContext);
};
