import React, { useEffect, useState } from "react";
import Navbar from "../components/NavBar";
import { useAuth } from "../AuthProvider";
import { useNavigate } from "react-router-dom";
import '../styles/Products.css';

const ProductosAnillosMatrimonio = () => {
  const navigate = useNavigate();
  const [productos, setProductos] = useState([]);
  const auth = useAuth();

  useEffect(() => {
    fetch("http://localhost:3000/producto", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": auth.token,
      },
    })
    .then((res) => res.json())
    .then((datos) => {
      console.log("Datos de la API:", datos);
      setProductos(datos.data);
    })
    .catch((error) => console.error("Error fetching products:", error));
  }, [auth.token]);

  const handleImageClick = (className, productName, imageUrl) => {
    console.log("Clicked:", className, productName, imageUrl);
    navigate("/productoSeleccionado", {
      state: { className, productName, imageUrl }
    });
  };

  return (
    <div>
      <Navbar />
      <h3 className="titlePRODUCTOS">ANILLOS DE MATRIMONIO</h3>
      <div className="containerPRODCUTOS">
        {productos.map((producto) => (
          <div 
            key={producto.ID_PRODUCTO} 
            className={`opcionPROD ${producto.ID_PRODUCTO}`} 
            onClick={() => handleImageClick(`anilloM${producto.ID_PRODUCTO}`, producto.DESCRIPCION, producto.IMAGEN)}
          >
            <div className="productoTexto">{producto.DESCRIPCION}</div>
            <div 
              style={{ backgroundImage: "url( `${process.env.REACT_APP_API_EP}{producto.IMAGEN}`)", backgroundSize: 'cover', backgroundPosition: 'center' }}
            ></div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductosAnillosMatrimonio;
