import React from "react";
import { useLocation } from "react-router-dom";
import Navbar from "../components/NavBar";
import '../styles/Products.css';

const ProductoSeleccionado = () => {
  const location = useLocation();
  const { className, productName, imageUrl } = location.state || {};
 // const [carrito, setCarrito] = useState({});

  console.log("Location State:", location.state);  // Verificación de los datos recibidos
  console.log("Location State:", location.state);

/*const agregarProdCarrito = ()=>{

}
*/
  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="productTitle">{productName ? productName : "No se seleccionó ningún producto"}</h2>
        {className ? (
          <div>
            <div className={`imagenProdSelecionado ${className}`} style={{ backgroundImage: `url(${imageUrl})` }}></div>
            <button className="btn-submitImgSel">Agregar al Carrito</button>
          </div>
        ) : (
          <p>No se seleccionó ninguna imagen.</p>
        )}
      </div>
    </div>
  );
};

export default ProductoSeleccionado;
