import React from "react";
import { useLocation } from "react-router-dom";
import Navbar from "../components/NavBar";
import '../styles/Products.css';

const ProductoSeleccionado = () => {
  const location = useLocation();
  const { producto } = location.state || {};

  console.log("Location State:", location.state);  // Verificación de los datos recibidos

  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="tituloPrdSel">{producto ? producto.DESCRIPCION : "No se seleccionó ningún producto"}</h2>
        {producto ? (
          <div>
            <img 
              className="imagenProdSelecionado" 
              src={`${process.env.REACT_APP_URL_IMG}${producto.IMAGEN}`} 
              alt={producto.DESCRIPCION} 
            />
            <div className="productoTexto">{producto.DESCRIPCION}</div>
            <button className="btn-submitImgSel">Agregar al Carrito</button>
          </div>
        ) : (
          <p>No se seleccionó ninguna imagen.</p>
        )}
      </div>
    </div>
  );
};

export default ProductoSeleccionado;
