import React from "react";
import Navbar from "../components/NavBar";
import "../styles/Products.css";

const Carrito = ({ carrito }) => {
    console.log("Carrito actual:", carrito);
  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="tituloPrdSel">Carrito de Compras</h2>
        {carrito && carrito.length === 0 ? (
            
          <p>No hay productos en el carrito.</p>
          
        ) : (
            
          <div>
            
            {carrito.map((producto, index) => (
              <div key={index} className="productoEnCarrito">
                <img
                  className="imagenProdSelecionado"
                  src={producto.imageUrl}
                  alt={producto.productName}
                />
                <div className="infoProducto">
                  <h3>{producto.productName}</h3>
                  <p>Precio: ${producto.costo}</p>
                </div>
              </div>
            ))}
            <button className="btn-submitImgSel">Realizar Compra</button>
          </div>
        )}
        
      </div>
    </div>
  );
};

export default Carrito;
