import React, { useEffect, useState } from "react";
import Navbar from "../components/NavBar";
import { useAuth } from "../AuthProvider";
import { useNavigate } from "react-router-dom";
import '../styles/Products.css';

const ProductosAnillosMatrimonio = () => {
  const navigate = useNavigate();
  const [productos, setProductos] = useState([]);
  const auth = useAuth();

  useEffect(() => {
    console.log("Token:", auth.token);
    fetch("http://localhost:3000/producto", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": auth.token,
      },
    })
    .then((res) => res.json())
    .then((datos) => {
      console.log("Datos de la API:", datos);  // Para verificar los datos recibidos
      setProductos(Object.values(datos));
    })
    .catch((error) => console.error("Error fetching products:", error));
  }, [auth.token]);

  const handleImageClick = (className, productName) => {
    console.log("Clicked:", className, productName);  // Verificación de los datos pasados
    navigate("/productoSeleccionado", {
      state: { className, productName }
    });
  };

  return (
    <div>
      <Navbar />
      <h3 className="titlePRODUCTOS">ANILLOS DE MATRIMONIO</h3>
      <div className="containerPRODCUTOS">
        {productos.map((producto) => (
          <div key={producto.ID_PRODUCTO} className={`opcionPROD anilloM${producto.ID_PRODUCTO}`} onClick={() => handleImageClick(`anilloM${producto.ID_PRODUCTO}`, producto.DESCRIPCION)}>
            <div className="productoTexto">{producto.DESCRIPCION}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductosAnillosMatrimonio;
