import React from "react";
import Navbar from "../components/NavBar";
import { useAuth } from "../AuthProvider";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import '../styles/Products.css';

const ProductosAnillosMatrimonio = () => {
  const navigate = useNavigate();
  const [productos, setProductos] = useState([]);
  const auth = useAuth();

  useEffect(() => {
    console.log("loquesea" + auth.token);
    fetch("http://localhost:3000/producto", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": auth.token,
      },
    }).then((res) => res.json()).then((datos) => setProductos(Object.values(datos)));
  }, []);

  const handleImageClick = (className, productName) => {
    navigate("/productoSeleccionado", {
      state: { className, productName }
    });
  };

  return (
    <div>
      <Navbar />

      <div className="containerPRODCUTOS">
        {productos.map((producto) => (
          <div key={producto.ID_PRODUCTO} className="opcionPROD anilloM1" onClick={() => handleImageClick('anilloM1', '{producto.DESCRIPCION}')}>
            <div className="productoTexto">{producto.DESCRIPCION}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductosAnillosMatrimonio;
