import React, { createContext, useContext, useState, useEffect } from "react";

// Crear el contexto
const CarritoContext = createContext();

// Hook personalizado para usar el contexto
export const useCarrito = () => useContext(CarritoContext);

// Proveedor del contexto
export const CarritoProvider = ({ children }) => {
  const [carrito, setCarrito] = useState([]);
  const [carritoCount, setCarritoCount] = useState(0);

  useEffect(() => {
    // Obtener el carrito del localStorage al cargar el componente
    const carritoLocalStorage = JSON.parse(localStorage.getItem("carrito")) || [];
    setCarrito(carritoLocalStorage);
    setCarritoCount(carritoLocalStorage.length);
  }, []);

  // Función para actualizar el carrito
  const actualizarCarrito = (nuevoCarrito) => {
    setCarrito(nuevoCarrito);
    setCarritoCount(nuevoCarrito.length);
    localStorage.setItem("carrito", JSON.stringify(nuevoCarrito));
  };

  return (
    <CarritoContext.Provider value={{ carrito, carritoCount, actualizarCarrito }}>
      {children}
    </CarritoContext.Provider>
  );
};
