import React from "react";
import Navbar from "../components/NavBar";
import { useNavigate } from "react-router-dom";
import "../styles/Products.css";

const Carrito = () => {
  // Obtener el carrito del localStorage
  const carrito = JSON.parse(localStorage.getItem("carrito")) || [];
  const navigate = useNavigate();

  const handleRealizarCompra = () => {
    // Redirigir a la página de factura
    navigate("/factura");
  };

  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="tituloPrdSel">Carrito de Compras</h2>
        {carrito.length === 0 ? (
          <p>No hay productos en el carrito.</p>
        ) : (
          <div>
            <table className="tablaCarrito">
              <thead>
                <tr>
                  <th>Imagen</th>
                  <th>Nombre</th>
                  <th>Precio</th>
                </tr>
              </thead>
              <tbody>
                {carrito.map((producto, index) => (
                  <tr key={index}>
                    <td>
                      <img
                        className="imagenProdSelecionado"
                        src={producto.imageUrl}
                        alt={producto.productName}
                      />
                    </td>
                    <td>{producto.productName}</td>
                    <td>${producto.costo}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <button
              className="btn-submitImgSel"
              onClick={handleRealizarCompra}
            >
              Realizar Compra
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Carrito;
