import React, { useState, useEffect } from "react";
import { useAuth } from "../AuthProvider";
import Navbar from "../components/NavBar";
import '../styles/CreateProduct.css';
import {categoryApi, materialApi} from "../common";
import toastr from "toastr";

const CreateProduct = () => {

    const auth = useAuth();

    const [product, setProduct] = useState({
        name: '',
        quantity: '',
        cost: '',
        margin: '',
        category: '',
        material: '',
        description: '',
        image: '',
    });
    const [categories, setCategories] = useState([]);
    const [materiales, setMateriales] = useState([]);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setProduct({
            ...product,
            [name]: value,
        });
    };

    const [mensaje, setMensaje] = useState("");


    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await fetch(categoryApi.getAllCategories.url, {
                    method: categoryApi.getAllCategories.method,
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${auth.token}`,
                    },
                });

                const data = await response.json();

                if (response.ok) {
                    setCategories(data.categories);
                } else if (response.status === 500) {
                    setMensaje(data.error);
                } else {
                    auth.logOut();
                    toastr.info(data.message);
                }
            } catch (error) {
                setMensaje('Ocurrió un error, al cargar las categorías');
            }
        };

        fetchCategories();
    }, [auth.token]);

    useEffect(() => {
        const fetchMateriales = async () => {
            try {
                const response = await fetch(materialApi.getAllMateriales.url, {
                    method: materialApi.getAllMateriales.method,
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${auth.token}`,
                    },
                });

                const data = await response.json();

                if (response.ok) {
                    setMateriales(data.materiales);
                } else if (response.status === 500) {
                    setMensaje(data.error);
                } else {
                    auth.logOut();
                    toastr.info(data.message);
                }
            } catch (error) {
                setMensaje('Ocurrió un error, al cargar los materiales');
            }
        };

        fetchMateriales();
    }, [auth.token]);

    const handleSubmitEvent = async (e) => {
        e.preventDefault();

        if (
            product.descripcion.trim() !== "" &&
            product.costo.trim() !== "" &&
            product.peso.trim() !== "" &&
            product.margen_ganancia.trim() !== "" &&
            product.cantidad.trim() !== "" &&
            product.id_categoria.trim() !== "" &&
            product.id_material.trim() !== ""
        ) {
            try {
                const response = await fetch("http://localhost:3000/producto", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${auth.token}`,
                    },
                    body: JSON.stringify(product),
                });
                const data = await response.json();
                if (response.ok) {
                    setMensaje(data.message);
                    setProduct({
                        name: '',
                        quantity: '',
                        cost: '',
                        margin: '',
                        category: '',
                        material: '',
                        description: '',
                        image: ''
                    });
                } else {
                    setMensaje(data.message || "Error al registrar el producto");
                }
            } catch (error) {
                console.error("Error al registrar el producto:", error.message);
                setMensaje("Error de conexión");
            }
        } else {
            setMensaje("Todos los campos son obligatorios");
        }
    };

    return (
        <div>
            <Navbar />
            <div className="backgroundRP">
                <div className="containerProduct">
                    {<div id="mensaje">{mensaje}</div>}
                    <h2>Crear Producto</h2>
                    <form className="product-form" onSubmit={handleSubmitEvent}>
                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="name">Nombre:</label>
                                <input type="text" id="name" name="name" value={product.name} onChange={handleChange} required />
                            </div>
                            <div className="form-group">
                                <label htmlFor="quantity">Cantidad:</label>
                                <input type="number" id="quantity" name="quantity" value={product.quantity} onChange={handleChange} required />
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="cost">Costo:</label>
                                <input type="number" id="cost" name="cost" value={product.cost} onChange={handleChange} required />
                            </div>
                            <div className="form-group">
                                <label htmlFor="margin">Margen de Ganancia:</label>
                                <input type="number" id="margin" name="margin" value={product.margin} onChange={handleChange} required />
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="category">Categoría:</label>
                                <select id="category" name="category" value={product?.category} onChange={handleChange} required>
                                    <option value="">Selecciona una categoría</option>
                                    <option value="joyeria">Joyería</option>
                                    <option value="relojes">Relojes</option>
                                    <option value="anillos">Anillos</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label htmlFor="material">Material:</label>
                                <select id="material" name="material" value={product.material} onChange={handleChange} required>
                                    <option value="">Selecciona un material</option>
                                    <option value="oro">Oro</option>
                                    <option value="plata">Plata</option>
                                    <option value="platino">Platino</option>
                                </select>
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="description">Descripción:</label>
                                <textarea id="description" name="description" value={product.description} onChange={handleChange} required />
                            </div>
                            <div className="form-group">
                                <label htmlFor="image">Imagen:</label>
                                <input type="file" id="image" name="image"  required />
                            </div>
                        </div>

                        <div className="form-row">
                            <button type="submit" className="btn-create">Crear Producto</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default CreateProduct;
