import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import Navbar from "../components/NavBar";

import "../styles/Products.css";

const Carrito = () => {
    const location = useLocation();
    const { className, productName, imageUrl, costo } = location.state || {};

  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="tituloPrdSel">Carrito de Compras</h2>
        {carrito && carrito.length === 0 ? (
          <p>No hay productos en el carrito.</p>
        ) : (
          <div>
            <table className="tablaCarrito">
              <thead>
                <tr>
                  <th>Imagen</th>
                  <th>Nombre</th>
                  <th>Precio</th>
                </tr>
              </thead>
              <tbody>
                {carrito.map((producto, index) => (
                  <tr key={index}>
                    <td>
                      <img
                        className="imagenProdSelecionado"
                        src={producto.imageUrl}
                        alt={producto.productName}
                      />
                    </td>
                    <td>{producto.productName}</td>
                    <td>${producto.costo}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <button className="btn-submitImgSel" onClick={realizarCompra}>
              Realizar Compra
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Carrito;
