    console.log(setCarrito);
    
    console.log(`Agregado al carrito: ${productName}, costo: ${costo}`);
*/
  
  };
  const mostrarCarrito = ()=>{
    setCarrito(localStorage.getItem("carrito") || {});

    carrito.productos = []

}

  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="tituloPrdSel">{productName ? productName : "No se seleccionó ningún producto"}</h2>
        {className && imageUrl ? (
          <div>
            <img
              className="imagenProdSelecionado"
              src={imageUrl}
              alt={productName}
            />
            <div className="costoProd">${costo}</div>
            <button className="btn-submitImgSel" onClick={agregarAlCarrito}>Agregar al Carrito</button>

          </div>

        ) : (
          <p>No se seleccionó ninguna imagen.</p>
        )}
      </div>
    </div>
  );
};

export default ProductoSeleccionado;
