import React, { useEffect, useState } from 'react';
import { useAuth } from '../AuthProvider';
import Navbar from '../components/NavBar';
import "../styles/ListProduct.css";

const ProductList = () => {
  const [productos, setProductos] = useState([]);
  const [mensaje, setMensaje] = useState('');
  const auth = useAuth();

  useEffect(() => {
    const fetchProductos = async () => {
      try {
        const response = await fetch('http://localhost:3000/producto', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${auth.token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setProductos(data.data);
        } else {
          throw new Error('Error al cargar los productos');
        }
      } catch (error) {
        console.error('Error al obtener los productos:', error.message);
        setMensaje('Error al cargar los productos');
      }
    };

    fetchProductos();
  }, [auth.token]);

  return (
    <div>
      <Navbar />
      <div className="backgroundPL">
        <div className="containerPL">
          <h2>Lista de Productos</h2>
          {mensaje && <div id="mensaje">{mensaje}</div>}
          <table className="tablePL">
            <thead>
              <tr>
                <th>ID</th>
                <th>Descripción</th>
                <th>Costo</th>
                <th>Peso</th>
                <th>Margen de Ganancia</th>
                <th>Cantidad</th>
                <th>Categoría</th>
                <th>Material</th>
                <th>Imagen</th>
              </tr>
            </thead>
            <tbody>
              {productos.map((producto) => (
                <tr key={producto.id_producto}>
                  <td>{producto.id_producto}</td>
                  <td>{producto.descripcion}</td>
                  <td>{producto.costo}</td>
                  <td>{producto.peso}</td>
                  <td>{producto.margen_ganancia}</td>
                  <td>{producto.cantidad}</td>
                  <td>{producto.id_categoria}</td>
                  <td>{producto.id_material}</td>
                  <td>
                    {producto.imagen && (
                      <img
                        src={`http://localhost:3000/uploads/${producto.imagen}`}
                        alt={producto.descripcion}
                        className="product-image"
                      />
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ProductList;
