import React from "react";
import { NavLink } from "react-router-dom";
import { useAuth } from "../AuthProvider";
import { useCarrito } from "../components/CarritoContext";
import "../styles/NavBar.css";

const NavBar = () => {
  const auth = useAuth();
  const { carrito } = useCarrito();

  // Calcular la cantidad total de productos en el carrito
  const totalProductos = carrito.reduce((total, item) => total + item.cantidad, 0);

  return (
    <div>
      <nav>
        <div className="navbar">
          <div className="logo">
            <NavLink to="/dashboard">
              <h1>JOYERIA D'LAURA</h1>
            </NavLink>
          </div>
          <div className="menu-items">
            <ul>
              <li>
                <div>Administrar</div>
              </li>
              <li style={{ margin: '0px 0px 0px 10px' }}>
                <NavLink to="/Categorias">Categor√≠as</NavLink>
              </li>
              {auth.user && (  // Solo mostrar el carrito si hay un usuario autenticado
                <li>
                  {totalProductos > 0 ? (
                    <NavLink to="/carrito">Carrito ({totalProductos})</NavLink>
                  ) : (
                    <NavLink to="/carrito">Carrito</NavLink>
                  )}
                </li>
              )}
              <li>
                <a href="#" onClick={() => auth.logOut()}>
                  Salir
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default NavBar;
