import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import Navbar from "../components/NavBar";
import '../styles/Products.css';

const ProductoSeleccionado = () => {
  const location = useLocation();
  const { className, productName, imageUrl, costo } = location.state || {};

  // Estado local para simular el carrito
  const [carrito, setCarrito] = useState([]);

  const agregarAlCarrito = () => {
    // Crea un objeto con la información del producto seleccionado
    const producto = {
      className,
      productName,
      imageUrl,
      costo
    };

    // Agrega el producto al carrito (simulado con un array en este caso)
    setCarrito([...carrito, producto]);

    // Muestra un mensaje (puedes quitar esto en producción)
    console.log(`Agregado al carrito: ${productName}, costo: ${costo}`);

    // Puedes redirigir a la página del carrito u otra página aquí si es necesario
    // navigate('/carrito');
  };

  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="tituloPrdSel">{productName ? productName : "No se seleccionó ningún producto"}</h2>
        {className && imageUrl ? (
          <div>
            <img 
              className="imagenProdSelecionado" 
              src={imageUrl} 
              alt={productName} 
            />
            <div className="costoProd">${costo}</div>
            <button className="btn-submitImgSel" onClick={agregarAlCarrito}>Agregar al Carrito</button>
          </div>
        ) : (
          <p>No se seleccionó ninguna imagen.</p>
        )}
      </div>
    </div>
  );
};

export default ProductoSeleccionado;
