import React from "react";
import { useLocation } from "react-router-dom";
import Navbar from "../components/NavBar";
import '../styles/Products.css';

const ProductoSeleccionado = () => {
  const location = useLocation();
  const { className, productName } = location.state || {};

  return (
    <div className="prodSeleccionadoContainer">
      <Navbar />
      <h2>Producto Seleccionado: {productName ? productName : "No se seleccionó ningún producto"}</h2>
      {className ? (
        <div>
          <div className={`imagenProdSelecionado ${className}`}></div>
          <button className="btn-submitImgSel">Agregar al Carrito</button>
        </div>
      ) : (
        <p>No se seleccionó ninguna imagen.</p>
      )}

    </div>
  );
};

export default ProductoSeleccionado;
