import React, { useEffect, useState } from "react";
import Navbar from "../components/NavBar";
import { useAuth } from "../AuthProvider";
import { useNavigate } from "react-router-dom";
import '../styles/Products.css';

const ProductosAnillosMatrimonio = () => {
  const navigate = useNavigate();
  const [productos, setProductos] = useState([]);
  const auth = useAuth();

  useEffect(() => {
    fetch("http://localhost:3000/producto", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": auth.token,
      },
    })
      .then((res) => res.json())
      .then((datos) => {
        const productosFiltrados = datos.data.filter(producto => producto.ID_CATEGORIA === '204');
        setProductos(productosFiltrados);
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, [auth.token]);

  const handleImageClick = (className, productName, imageUrl) => {
    console.log("handleImageClick params:", className, productName, imageUrl); // Verificaci칩n de par치metros
    navigate("/productoSeleccionado", {
      state: { className, productName, imageUrl }
    });
  };

  return (
    <div>
      <Navbar />
      <h3 className="titlePRODUCTOS">ANILLOS DE MATRIMONIO</h3>
      <div className="containerPRODCUTOS">
        {productos.map((producto) => {
          const className = `${producto.ID_PRODUCTO}`;
          const productName = producto.DESCRIPCION;
          const imageUrl = `${process.env.REACT_APP_URL_IMG}${producto.IMAGEN}`;

          return (
            <div
              key={producto.ID_PRODUCTO}
              className={className}
              onClick={() => handleImageClick(className, productName, imageUrl)}
            >
              <div>
                <img 
                  className="imagenProdSelecionado" 
                  src={imageUrl} 
                  alt={productName} 
                />
                <div className="productoTexto">{productName}</div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductosAnillosMatrimonio;
