import React from "react";
import Navbar from "../components/NavBar";
import { useAuth } from "../AuthProvider";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import '../styles/Products.css';

const ProductosAnillosMatrimonio = () => {
  const navigate = useNavigate();
  const [productos, setProductos] = useState([]);
  const auth = useAuth();

  useEffect(() => {
    fetch("http://localhost:3000/producto", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${auth.token}`,
      },
    }).then((res) => res.json()).then((datos) => setProductos(datos));
  }, []);

  const handleImageClick = (className, productName) => {
    navigate("/productoSeleccionado", {
      state: { className, productName }
    });
  };

  return (
    <div>
      <Navbar />
      <h3 className="titlePRODUCTOS">ANILLOS DE MATRIMONIO</h3>
      <div className="containerPRODCUTOS">
        {producto.map((producto) => (

          <div className="opcionPROD anilloM1" onClick={() => handleImageClick('anilloM1', '${producto.descripcion}')}>
            <div className="productoTexto">${producto.descripcion}</div>
          </div>
        ))};
      </div>
    </div>
  );
};

export default ProductosAnillosMatrimonio;
