import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useAuth } from "../AuthProvider";
import Navbar from "../components/NavBar";
import "../styles/Registers.css";

const RegisterFactura = () => {
  const location = useLocation();
  const total = location.state?.total || 0;
  const iva = total * 0.19;
  const totalPagar = total + iva;

  const [factura, setFactura] = useState({
    id_cliente: ""
  });

 /* const [detalleFactura, setDetalleFactura] = useState({
    id_factura:"",
    id_producto:"",
    cantidad:"",
    vr_unitario: total,
    iva:iva,
    total_pagar: totalPagar,
  });
*/
  const [mensaje, setMensaje] = useState("");
  const { user, token } = useAuth();
  console.log(user);

  useEffect(() => {
    // Actualizar el subtotal y el total a pagar si cambia el total desde la ubicación
    setDetalleFactura((prevFactura) => ({
      ...prevFactura,
      subtotal: total,
      total_pagar: totalPagar,
    }));
  }, [total]);

  const handleSubmitEvent = async (e) => {
    e.preventDefault();

    if (factura.id_cliente.trim() !== "" && factura.id_usuario.trim() !== "") {
      try {
        const response = await fetch("http://localhost:3000/factura", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(factura),
        });
        const data = await response.json();
        if (response.ok) {
          setMensaje(data.message);
          setFactura({
            id_cliente: "",
            id_usuario: "",
          });
        } else {
          setMensaje(data.message || "Error al registrar la factura");
        }
      } catch (error) {
        console.error("Error al registrar la factura:", error.message);
        setMensaje("Error de conexión");
      }
    } else {
      setMensaje("Todos los campos son obligatorios");
    }
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setFactura((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div>
      <Navbar />
      <div className="backgroundRCli">
        <div className="containerRCli">
          <div id="mensaje">{mensaje}</div>
          <h2 className="tituloU-C">Confirmación de Compra</h2>
          <form id="datos" onSubmit={handleSubmitEvent}>
            <div>
              {user && (
                <>
                  <p>Usuario: {user.NOMBRE_USUARIO}</p>
                  <p>Nombre: {user.NOMBRE} {user.APELLIDOS}</p>
                  <p>Dirección: {user.DIRECCION} </p>
                  <p>Barrio: {user.BARRIO} </p>
                  <p>Teléfono: {user.TELEFONO}</p>
                </>
              )}
              <p>Subtotal: ${detalleFactura.subtotal.toLocaleString("es-ES")}</p>
              <p>IVA: ${iva.toLocaleString("es-ES")}</p>
              <p>Total a Pagar: ${detalleFactura.total_pagar.toLocaleString("es-ES")}</p>
              <button className="btn-submitRM">Realizar Compra</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RegisterFactura;
