import { useState } from "react";
import { useAuth } from "../AuthProvider";
import "../styles/Login.css";

const Login = () => {
  const [input, setInput] = useState({
    username: "",
    password: "",
  });
  const [mensaje, setMensaje] = useState("");
  const { loginAction } = useAuth(); // Obtener loginAction desde el contexto de autenticación

  const handleSubmitEvent = async (e) => {
    e.preventDefault();
    if (input.username !== "" && input.password !== "") {
      try {
        await loginAction(input);
      } catch (error) {
        setMensaje("Usuario y contraseña incorrectos");
        console.error("Error al iniciar sesión:", error.message);
      }
    } else {
      alert("El usuario y la contraseña son obligatorios");
    }
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setInput((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="backgroundLG">
      <h1 className="title">GEMPRO</h1>
      <div className="login-containerLG">
        <div id="mensaje">{mensaje}</div>
        <h2>Inicio de Sesión</h2>
        <form id="datos" onSubmit={handleSubmitEvent}>
          <div className="form_controlLG">
            <label htmlFor="user-name">Usuario</label>
            <input
              type="text"
              id="user-name"
              name="username"
              placeholder="Usuario"
              onChange={handleInput}
            />
          </div>
          <div className="form_controlLG">
            <label htmlFor="password">Contraseña</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Contraseña"
              onChange={handleInput}
            />
          </div>
          <button className="btn-submitLG">Iniciar sesión</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
