import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { useAuth } from "../AuthProvider";
import { useCarrito } from "../components/CarritoContext";
import "../styles/NavBar.css";

const NavBar = () => {
  const auth = useAuth();
  const { carrito } = useCarrito();

  const totalProductos = carrito.reduce((total, item) => total + item.cantidad, 0);
  const userType = auth.user ? auth.user.ID_TIPO : null;

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    auth.logOut();
    setIsDropdownOpen(false); // Close the dropdown when logging out
  };

  return (
    <div>
      <nav>
        <div className="navbar">
          <div className="logo">
            <NavLink to="/dashboard">
              <h1>JOYERIA D'LAURA</h1>
            </NavLink>
          </div>
          <div className="menu-items">
            <ul>
              {userType === "1" && (
                <li className="dropdown">
                  <div onClick={toggleDropdown}>Administrar</div>
                  {isDropdownOpen && (
                    <ul className="dropdown-menu">
                      <li>
                        <NavLink to="/admin/opcion1">Opción 1</NavLink>
                      </li>
                      <li>
                        <NavLink to="/admin/opcion2">Opción 2</NavLink>
                      </li>
                      <li>
                        <NavLink to="/admin/opcion3">Opción 3</NavLink>
                      </li>
                    </ul>
                  )}
                </li>
              )}
              <li style={{ margin: '0px 0px 0px 10px' }}>
                <NavLink to="/Categorias">Categorías</NavLink>
              </li>
              <li>
                <NavLink to="/About">About</NavLink>
              </li>
              {auth.user && (
                <li>
                  {totalProductos > 0 ? (
                    <NavLink to="/carrito">Carrito ({totalProductos})</NavLink>
                  ) : (
                    <NavLink to="/carrito">Carrito</NavLink>
                  )}
                </li>
              )}
              <li>
                {auth.user ? (
                  <a href="#" onClick={handleLogout}>Salir</a>
                ) : (
                  <NavLink to="/Login">Ingresar</NavLink>
                )}
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default NavBar;
