import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import Navbar from "../components/NavBar";
import "../styles/Products.css";

const ProductoSeleccionado = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { idProduct, productName, imageUrl, costo, margen_ganancia } = location.state || {};

  // Estado local para el producto seleccionado
  const [productoSeleccionado, setProductoSeleccionado] = useState(null);
  const [cantidad, setCantidad] = useState(1);

  // Cargar el producto seleccionado cuando el componente se monta
  useState(() => {
    if (location.state) {
      setProductoSeleccionado({
        idProduct,
        productName,
        imageUrl,
        costo,
        cantidad,
        margen_ganancia
      });
    }
  }, [location.state]);
  //console.log(location.state);
  const agregarAlCarrito = () => {
    if (!productoSeleccionado) return;

    // Obtener el carrito actual del localStorage o inicializarlo como un array vacío
    const carritoActual = JSON.parse(localStorage.getItem("carrito")) || [];

    // Agregar el producto seleccionado al carrito
    carritoActual.push(productoSeleccionado);

    // Guardar el carrito actualizado en el localStorage
    localStorage.setItem("carrito", JSON.stringify(carritoActual));
    navigate(-1);

    // Mostrar un mensaje en la consola (puedes quitar esto en producción)
    // console.log(`Agregado al carrito: ${productName}, costo: ${costo}`);
  };

  return (
    <div>
      <Navbar />
      <div className="prodSeleccionadoContainer">
        <h2 className="tituloPrdSel">
          {productName ? productName : "No se seleccionó ningún producto"}
        </h2>
        {idProduct && imageUrl ? (
          <div>
            <img
              className="imagenProdSelecionado"
              src={imageUrl}
              alt={productName}
            />
            <div className="cantidadProdSel" style={{ display: 'flex',justifyContent: 'center', alignItems: 'center' }}>
              <label style={{ marginRight: '20px' }}>Cantidad:</label>
              <select value={cantidad} onChange={(e) => setCantidad(parseInt(e.target.value))}>
                {[...Array(10).keys()].map((num) => (
                  <option key={num + 1} value={num + 1}>{num + 1}</option>
                ))}
              </select>
            </div>
            <div className="costoProdSel"> Valor Unitario: $ {((costo * margen_ganancia) + costo).toLocaleString('es-ES')}</div>
            <div className="costoProdSel">Valor Total: </div>
            <button className="btn-submitImgSel" onClick={agregarAlCarrito}>
              Agregar al Carrito
            </button>
          </div>
        ) : (
          <p>No se seleccionó ninguna imagen.</p>
        )}
      </div>
    </div>
  );
};

export default ProductoSeleccionado;
